using CommunityToolkit.Mvvm.ComponentModel;using Microsoft.UI.Dispatching;using Microsoft.UI.Xaml;using System.Reflection;namespace DeepWork.ViewModels.Pages{	public partial class SettingsViewModel : ObservableObject	{		[ObservableProperty]		private string _appVersion = string.Empty;		[ObservableProperty]		private ApplicationTheme _currentTheme;		public SettingsViewModel()		{			_currentTheme = Application.Current.RequestedTheme;			AssemblyName assemblyName = Assembly.GetExecutingAssembly().GetName();			_appVersion = assemblyName.Name + "-" + assemblyName.Version.ToString();		}	}}